import type Web3 from "web3"
import type { Address } from "@rarible/types"
import type { AbiItem } from "../../../../common/abi-item"

export function createErc1155TransferProxyContract(web3: Web3, address?: Address) {
	return new web3.eth.Contract(erc1155TransferProxyAbiItem, address)
}

export async function deployErc1155TransferProxy(web3: Web3) {
	const contract = createErc1155TransferProxyContract(web3)
	const [address] = await web3.eth.getAccounts()

	return contract.deploy({data: erc1155TransferProxyBytecode})
		.send({ from: address, gas: 5000000, gasPrice: "0" })
}

